'use strict';
const escpos = require('../');

const device  = new escpos.USB();
// const device  = new escpos.Network('localhost');
// const device  = new escpos.Serial('/dev/usb/lp0');
const printer = new escpos.Printer(device);

device.open(function(err){

  printer
  .font('b')
  .align('lt')
  .style('normal')
  .size(1, 1)
  .text('ΠΑΡΑΓΓΕΛΙΑ #156')
  .size(2, 1)
  .style('b')
  .text('ΠΛΑΤ3')
  .style('normal')
  .size(1, 1)
  .text('---------------')
  .control('lf') //line feed
  .text('2 ΠΑΓΩΤΟ ΦΡ')
  .align('rt')
  .text('4.5')
  .align('lt')
  .control('lf') //line feed
  .text('1 ΦΕΜ')
  .align('rt')
  .text('3')
  .align('lt')
  .control('lf') //line feed
  .text('2 ΦΡΑΠΠΕ ΓΛ ΓΑΛΑ')
  .align('rt')
  .text('6')
  .align('lt')
  .control('lf') //line feed
  .text('---------------')
  .align('rt')
  .style('b')
  .text('13.5')
  .cut()
  .close();
  
});
