const { USB, Printer } = require('..');

(async () => {

  const device  = await USB.getDevice();
  const printer = await Printer.create(device);

  await printer
  //.font('a')
  //.align('lt')
  //.style('normal')
  .size(1, 1)
  .text('ΚΑΛΗΣΠΕΡΑ');
  await printer.cut();
  await printer.close();

  console.log('print job done');

})();

