var printer = require("./node-thermal-printer");
printer.init({
  type: 'epson',
  interface: '/dev/usb/lp0'
});
console.log('start');
printer.println("Hello world");
printer.execute(function(err){
  if (err) {
    console.error("Print failed", err);
  } else {
   console.log("Print done");
  }
});
console.log('end');